import React, { Component, useState } from 'react'
import { useForm } from 'react-hook-form'
import { useDispatch, useSelector } from 'react-redux'
import {GetPokemon} from '../actions/pokemonActions'
import Modal from './Modal'
import _ from 'lodash'
import Axios from 'axios'

const Pokemon = (props) => {
    const pokemonName = props.match.params.pokemon
    const dispatch = useDispatch()
    const pokemonState = useSelector(state => state.Pokemon)
    const [isOpen, setIsOpen] = useState(false)
    const [name, setName] = useState('')
    const [nickname, setNickname] = useState('')
    const [id_pokemon, setIdPokemon] = useState('')
    

    React.useEffect(() => {
        dispatch(GetPokemon(pokemonName))
    }, [])
    
    const ShowData = () => {
        if (!_.isEmpty(pokemonState.data[pokemonName])) {
            const pokeData = pokemonState.data[pokemonName]
            return (
                <div className="pokemon">
                    <div className="img">
                        <img src={pokeData.sprites.front_default} alt=""/>
                        <img src={pokeData.sprites.back_default} alt=""/>
                    </div>
                    <div className="pokemonWrapper">
                        <div className="item">
                            <h4>Types</h4>
                            {pokeData.types.map(el => {
                                return <p>{el.type.name}</p>
                            })}
                        </div>
                        <div className="item">
                            <h4>Abilities</h4>
                            {pokeData.abilities.map(el => {
                                return <p>{el.ability.name}</p>
                            })}
                        </div>
                    </div>
                    <div className="pokemonWrapper">
                        <div className="item">
                            <h4>Stats</h4>
                            {pokeData.stats.map(el => {
                                return <p>{el.stat.name} : {el.base_stat}</p>
                            })}
                        </div>
                        <div className="item">
                            <h4>Held Items</h4>
                            {pokeData.held_items.map(el => {
                                return <p>{el.item.name}</p>
                            })}
                        </div>
                    </div>
                    <div className="pokemonWrapper">
                        <div className="item">
                            <h4>Moves</h4>
                            {pokeData.moves.map(el => {
                                return <p>{el.move.name}</p>
                            })}
                        </div>
                        <div className="item">
                            <h4>Game Indices</h4>
                            {pokeData.game_indices.map(el => {
                                return <p>{el.version.name} : {el.game_index}</p>
                            })}
                        </div>
                    </div>
                    
                </div>
            )
        }

        if (pokemonState.loading) {
            return <h1 className="loading">Loading...</h1>
        }

        if (pokemonState.errorMsg !== "") {
            return <p>{pokemonState.errorMsg}</p>
        }

        return <p>error getting pokemon</p>
    }

    // const ShowDataId = () => {
    //     if (!_.isEmpty(pokemonState.data[pokemonName])) {
    //         const pokeDataId = pokemonState.data[pokemonName]
    //         return (
    //             <input className="form-control modal-input" type="hidden" name="id_pokemon" value={pokeDataId.id} onChange={(e) => {setIdPokemon(e.target.value)}}/>
    //         )
    //     }

    //     if (pokemonState.loading) {
    //         return <h1 className="loading">Loading...</h1>
    //     }

    //     if (pokemonState.errorMsg !== "") {
    //         return <p>{pokemonState.errorMsg}</p>
    //     }

    //     return <p>error getting pokemon</p>
    // }

    const submitNickname = () => {
        Axios.post("http://localhost:3001/api/insert", {
            nickname: nickname, 
            name: name, 
            
        }).then(() => {
            alert("successful insert")
            // console.log(nickname, name)
        })
    }

    // const { register } = useForm({
    //     defaultValues: {
    //       name: pokemonName,
    //     }
    //   })
      
    const { register, handleSubmit } = useForm();
    const onSubmit = data => {
        Axios.post("http://localhost:3001/api/insert", {
            nickname: data.nickname, 
            name: data.name, 
            
        }).then(() => {
            alert("successful insert")
            // console.log(nickname, name)
        })
    }

    return (
        <div className="container pokemonDetails">
            <div className="card">
                <div className="card-body">
                    <h2>{pokemonName}</h2>
                    <button className="btn btn-warning" onClick={() => setIsOpen(true)}>Catch Pokemon</button>
                    <Modal open={isOpen} onClose={() => setIsOpen(false)}>
                        <div>
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <input className="form-control modal-input" type="text" name="nickname" placeholder="Nickname" ref={register}/>

                                {/* <input className="form-control modal-input" type="text" name="name" value={pokemonName} onChange={(e) => {setName(e.target.value)}}/> */}

                                <input name="name" defaultValue={pokemonName} ref={register} />

                                {/* <button type="submit" class="btn btn-primary modal-save" onClick={submitNickname}>Save</button> */}
                                <input type="submit" class="btn btn-primary modal-save" />
                            </form>
                            
                        </div>
                    </Modal>
                    {ShowData()}
                </div>
            </div>
        </div>
    )
}

export default Pokemon
