import React, { useEffect } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import _ from 'lodash'
import { GetPokemonList } from '../actions/pokemonActions'
import {Link} from 'react-router-dom'
import ReactPaginate from 'react-paginate'

const PokemonList = () => {
    const dispatch = useDispatch()
    const [nextUrl, setNextUrl] = useState('')
    const [prevUrl, setPrevUrl] = useState('')
    const [loading, setLoading] = useState(true)
    const pokemonList = useSelector(state => state.PokemonList)

    useEffect(() => {
        FetchData(1)
    }, [])

    const FetchData = (page = 1) => {
        dispatch(GetPokemonList(page))
    }

    const next = async () => {
        setLoading(true)
        let data = await GetPokemonList(nextUrl)
        setNextUrl(data.next)
        setPrevUrl(data.previous)
        setLoading(false)
    }

    const prev = async () => {
        if (!prevUrl) return
        setLoading(true)
        let data = await GetPokemonList(prevUrl)
        setNextUrl(data.next)
        setPrevUrl(data.previous)
        setLoading(false)
    }

    if (pokemonList.loading) {
        return <p>Loading...</p>
    }

    const ShowData = () => {
        if (!_.isEmpty(pokemonList.data)) {
            return pokemonList.data.map(el => {
                return (
                    <div className="container">
                        <div className="card">
                            <div className="card-body">
                                <p>{el.name}</p>
                                <Link to={`/pokemon/${el.name}`}>View</Link>
                            </div>
                        </div>
                    </div>
                    
                )
            })   
        }

        if (pokemonList.errorMsg !== "") {
            return <p>{pokemonList.errorMsg}</p>
        }

        return <p>unable to get data</p>
    }

    return (
        <div>
            {ShowData()}
            <div className="button">
                            <button className="btn btn-primary" onClick={prev}>Previous</button>
                            <button className="btn btn-primary" onClick={next}>Next</button>
                        </div>
            {!_.isEmpty(pokemonList.data) && (
                <ReactPaginate 
                    pageCount={Math.ceil(pokemonList.count / 20)}
                    onPageChange={(data) => FetchData(data.selected + 1)}
                    pageRangeDisplayed={2}
                    marginPagesDisplayed={1}
                />
            )}
        </div>
    )
}

export default PokemonList
