import React, {useState, useEffect} from 'react'
import Axios from 'axios'
import { useForm } from 'react-hook-form'

export default function MyPokemonList() {
    const [myPokemonList, setMyPokemonList] = useState([])
    const [allPokemonList, setAllPokemonList] = useState([])
    const [pokemonList, setPokemonList] = useState([])
    const { register, handleSubmit } = useForm()

    useEffect(() => {
        Axios.get('http://localhost:3001/api/getjoin').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const deletePokemon = (pokemon) => {
        Axios.delete(`http://localhost:3001/api/delete/${pokemon.nickname}`)

        Axios.put("http://localhost:3001/api/update", {
            name: pokemonList.map((val) => {
                    if (val.name === pokemon.name) {
                        return val.name
                    }
                }),
            qty: pokemonList.map((val) => {
                    if (val.name === pokemon.name) {
                        return val.qty - 1
                    }
                }),
        })

    }

    return (
        <div className="container">
            {myPokemonList.map((val, key) => {
                return (
                    <div key={key} className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>

                            <input type="hidden" name="name" defaultValue={val.name} ref={register} />
                            <input type="hidden" name="qty" defaultValue={val.qty} ref={register} />

                            <button className="btn btn-sm btn-danger" onClick={() => [deletePokemon({nickname: val.nickname, name: val.name}), showPokemonQty(val.name)]}>Remove</button>
                            
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
