import React, {useState, useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {GetPokemon} from '../actions/pokemonActions'
import Axios from 'axios'
import { useForm } from 'react-hook-form'
import Modal from './Modal'

export default function MyPokemonList(props) {
    const [myPokemonList, setMyPokemonList] = useState([])
    const pokemonName = props.match.params.pokemon
    const dispatch = useDispatch()
    const pokemonState = useSelector(state => state.Pokemon)
    const [pokemonList, setPokemonList] = useState([])
    const { register, handleSubmit } = useForm()
    const [isOpen2, setIsOpen2] = useState(false)

    React.useEffect(() => {
        dispatch(GetPokemon(pokemonName))
    }, [])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/getmypokemon').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const showPokemonQty = (pokemon) => {
        Axios.get(`http://localhost:3001/api/get/${pokemon}`).then((response) => {
            setPokemonList(response.data)
        })
    }

    const deletePokemon = (pokemon) => {
        Axios.delete(`http://localhost:3001/api/delete/${pokemon.nickname}`)

        Axios.put("http://localhost:3001/api/update", {
            name: ShowDataName(pokemon.name),
            qty: ShowDataQty(pokemon.name),
        })
    }
   

    const ShowDataQty = (pokemon) => {
        return (
            pokemonList.map((val) => {
                if (val.name === pokemon) {
                    return val.qty - 1
                }
            })
        )
    }

    const ShowDataName = (pokemon) => {
        return(
            pokemonList.map((val) => {
                if (val.name === pokemon) {
                    return val.name
                }
            })    
        )
        
    }

    return (
        <div className="container">
            {myPokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>

                            <form>
                                <input type="hidden" name="qty" defaultValue={ShowDataQty(val.name)} ref={register} />

                            </form>


                            <button className="btn btn-sm btn-danger" onClick={() => [deletePokemon({name: val.name}), showPokemonQty(val.name), setIsOpen2(true, val.id)]}>Remove</button>

                            <ModalRemove open={isOpen2} onClose={() => setIsOpen2(false)}>
                            <button className="btn btn-sm btn-danger" onClick={() => [deletePokemon({nickname: val.nickname ,name: val.name}), showPokemonQty(val.name)]}>Remove</button>
                        </ModalRemove>
                            
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
