import React, {useState, useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {GetPokemon} from '../actions/pokemonActions'
import Axios from 'axios'
import { useForm } from 'react-hook-form'
import ModalRemove from './Modal'
import PokemonList from './PokemonList'

export default function MyPokemonList(props) {
    const [myPokemonList, setMyPokemonList] = useState([])
    const pokemonName = props.match.params.pokemon
    const dispatch = useDispatch()
    const pokemonState = useSelector(state => state.Pokemon)
    const [pokemonList, setPokemonList] = useState([])
    const [allPokemon, setPokemon] = useState([])
    const { register, handleSubmit } = useForm()
    const [isOpen3, setIsOpen3] = useState(false)

    React.useEffect(() => {
        dispatch(GetPokemon(pokemonName))
    }, [])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/getmypokemon').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const addPokemonQty = (pokemon) => {
        Axios.get(`http://localhost:3001/api/get/${pokemon}`).then((response) => {
            setPokemonList(response.data)
        })
    }

    // const getAllPokemon = () => {
    //     Axios.get('http://localhost:3001/api/get').then((response) => {
    //         setPokemon(response.data)
    //         console.log(response.data)
    //     })
    // }

   

    const ShowDataQty = (pokemon) => {
        return (
            pokemonList.map((val) => {
                if (val.name === pokemon) {
                    return val.qty - 1
                }
            })
        )
    }

    const ShowDataName = (pokemon) => {
        return(
            pokemonList.map((val) => {
                if (val.name === pokemon) {
                    return val.name
                }
            })    
        )
        
    }

    const deletePokemon = (pokemon) => {
        Axios.post(`http://localhost:3001/api/delete/${pokemon.nickname}`, {
            name: ShowDataName(pokemon.name),
            qty: ShowDataQty(),
        }).then(() => {
            alert("successful delete")
        })

        console.log(ShowDataQty(pokemon.name))
    }
        

    return (
        <div className="container">
            {myPokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>

                            <input type="hidden" name="qty" defaultValue={ShowDataQty()} ref={register} />

                            <button className="btn btn-sm btn-danger" onClick={() => [deletePokemon({nickname: val.nickname, name: val.name}), addPokemonQty(val.name), ShowDataName(val.name), ShowDataQty(val.name)]}>Remove</button>
                            
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
