import React, { useState, useEffect } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import _ from 'lodash'
import { GetPokemonList } from '../actions/pokemonActions'
import {Link} from 'react-router-dom'
import ReactPaginate from 'react-paginate'
import Axios from 'axios'

const PokemonList = () => {
    const dispatch = useDispatch()
    const pokemonList = useSelector(state => state.PokemonList)
    const [pokemonQty, setPokemonQty] = useState([])

    useEffect(() => {
        FetchData(1)
    }, [])

    const getPokemonQty = (pokemon) => {
        Axios.get(`http://localhost:3001/api/get/${pokemon}`).then((response) => {
            setPokemonQty(response.data)
        })
    }

    const FetchData = (page = 1) => {
        dispatch(GetPokemonList(page))
    }

    const ShowData = () => {
        if (pokemonList.loading) {
            return <h1 className="loading">Loading...</h1>
        }

        if (!_.isEmpty(pokemonList.data)) {
            return pokemonList.data.map(el => {
                return (
                    <div className="container">
                        <div className="card">
                            <div className="card-body">
                                <p><Link to={`/pokemon/${el.name}`}>{el.name}</Link></p>
                                <p>{getPokemonQty()}</p>
                            </div>
                        </div>
                    </div>
                    
                )
            })   
        }

        if (pokemonList.errorMsg !== "") {
            return <p>{pokemonList.errorMsg}</p>
        }

        return <p>unable to get data</p>
    }

    return (
        <div>
            {ShowData()}
            {!_.isEmpty(pokemonList.data) && (
                <ReactPaginate 
                    pageCount={Math.ceil(pokemonList.count / 10)}
                    pageRangeDisplayed={2}
                    marginPagesDisplayed={1}
                    onPageChange={(data) => FetchData(data.selected + 1)}
                    containerClassName={"container pagination"}
                    pageClassName={"btn btn-sm btn-primary"}
                    previousClassName={"btn btn-sm btn-primary"}
                    nextClassName={"btn btn-sm btn-primary"}
                />
            )}
        </div>
    )
}

export default PokemonList
