import React, {useState, useEffect} from 'react'
import Axios from 'axios'
import {Link} from 'react-router-dom'
import ReactPaginate from 'react-paginate'
import _ from 'lodash'

export default function PokemonList() {
    const [pokemonList, setPokemonList] = useState([])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setPokemonList(response.data)
        })
    }, [])

    return (
        <div className="container">
            {pokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p><Link to={`/pokemon/${val.name}`}>{val.name}</Link></p>
                            <p>{(val.qty == null) ? 0 : val.qty}</p>
                        </div>
                    </div>
                )
            })}
            {!_.isEmpty(pokemonList.data) && (
                <ReactPaginate 
                    pageCount={Math.ceil(pokemonList.count / 10)}
                    pageRangeDisplayed={2}
                    marginPagesDisplayed={1}
                    onPageChange={(data) => FetchData(data.selected + 1)}
                    containerClassName={"container pagination"}
                    pageClassName={"btn btn-sm btn-primary"}
                    previousClassName={"btn btn-sm btn-primary"}
                    nextClassName={"btn btn-sm btn-primary"}
                />
            )}
        </div>
    )
}
