import React, {useState, useEffect} from 'react'
import { useSelector } from 'react-redux'
import Axios from 'axios'

export default function MyPokemonList(props) {
    const [myPokemonList, setMyPokemonList] = useState([])
    const pokemonName = props.match.params.pokemon
    const pokemonState = useSelector(state => state.Pokemon)
    const [pokemonList, setPokemonList] = useState([])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/getmypokemon').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const deletePokemon = (pokemon) => {
        Axios.delete(`http://localhost:3001/api/delete/${pokemon}`)
        
    }

    const ShowDataQty = () => {
        return (
            pokemonList.map((val) => {
                
                    if (val.name === pokemonName) {
                        return val.qty + 1
                    }
                
            })
        )
    }

    return (
        <div className="container">
            {myPokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>
                            <button className="btn btn-sm btn-danger" onClick={() => {deletePokemon(val.id)}}>Remove</button>
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
