import React, {useState, useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {GetPokemon} from '../actions/pokemonActions'
import { useForm } from 'react-hook-form'
import Axios from 'axios'

export default function MyPokemonList(props) {
    const [myPokemonList, setMyPokemonList] = useState([])
    const pokemonName = props.match.params.pokemon
    const dispatch = useDispatch()
    const pokemonState = useSelector(state => state.Pokemon)
    const [pokemonList, setPokemonList] = useState([])
    const { register, handleSubmit } = useForm()

    React.useEffect(() => {
        dispatch(GetPokemon(pokemonName))
    }, [])

    useEffect(() => {
        Axios.get('http://localhost:3001/api/getmypokemon').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const onSubmit = data => {
        Axios.post("http://localhost:3001/api/insert", {
            nickname: data.nickname, 
            name: data.name,
            qty: data.qty, 
        }).then(() => {
            alert("successful insert")
        })
    }

    const deletePokemon = (data) => {
        Axios.delete(`http://localhost:3001/api/delete/${data.nickname}`, {
            name: data.name,
            qty: data.qty,
        }).then(() => {
            return (
                pokemonList.map((val) => {
                    if (val.name === pokemonName) {
                        return val.qty + 1
                    }
                })
            )
        })
        
    }

    const addPokemonQty = (pokemon) => {
        Axios.get(`http://localhost:3001/api/get/${pokemon}`).then((response) => {
            setPokemonList(response.data)
        })
    }

    const ShowDataQty = () => {
        
    }

    return (
        <div className="container">
            {myPokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>
                            <form >
                                <input type="hidden" name="nickname" defaultValue={val.nickname} ref={register}/>

                                <input type="hidden" name="name" defaultValue={val.name} ref={register} />

                                <input type="hidden" name="qty" defaultValue={ShowDataQty()} ref={register} />

                                <input type="submit" value="Remove" className="btn btn-sm btn-danger" onClick={() => {[deletePokemon(), addPokemonQty(val.name)]}} />
                            </form>
                            
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
