import React, { useState } from 'react'

const MODAL_STYLE = {
    position: 'sticky',
    top: '30%',
    left: '50%',
    backgroundColor: '#fff',
    padding: '50px',
    zIndex: '1000',
}

const OVERLAY_STYLES = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, .7)',
    zIndex: 1000,
}

export default function Modal({ open, children, onClose }) {
    if (!open) return null

    const [name, setName] = useState('')
    const [nicname, setNickname] = useState('')
    const [id_pokemon, setIdPokemon] = useState('')

    return (
        <>
            <div style={OVERLAY_STYLES}/>
            <div style={MODAL_STYLE}>
                <button className="btn-modal-close" data-toggle="modal" data-target="#staticBackdrop" onClick={onClose}>X</button>
                {children}
                <button type="submit" class="btn btn-primary modal-save">Save</button>

            </div>
        </>
    )
}
