import React from 'react'

const MODAL_STYLE = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: '#fff',
    padding: '50px',
    zIndex: '1000',
}

export default function Modal({ open, children, onClose }) {
    if (!open) return null

    return (
        <>
            <div>
                <div style={MODAL_STYLE}>
                    <button onClick={onClose}>X</button>
                    {children}
                </div>
                
            </div>
        </>
    )
}
