import React, {useState, useEffect} from 'react'
import Axios from 'axios'

export default function MyPokemonList() {
    const [myPokemonList, setMyPokemonList] = useState([])
    const [name, setName] = useState('')
    const [nickname, setNickname] = useState('')
    const [id_pokemon, setIdPokemon] = useState('')

    useEffect(() => {
        Axios.get('http://localhost:3001/api/get').then((response) => {
            setMyPokemonList(response.data)
        })
    }, [])

    const deletePokemon = (pokemon) => {
        Axios.delete(`http://localhost:3001/api/delete/${pokemon}`)

        setMyPokemonList([
            ...myPokemonList,
            { name: name, nickname: nickname, id_pokemon: id_pokemon }
        ])
    }

    return (
        <div className="container">
            {myPokemonList.map((val) => {
                return (
                    <div className="card">
                        <div className="card-body">
                            <p>Name : {val.name}</p>
                            <p>Nickname : {val.nickname}</p>
                            <p>ID Pokemon : {val.id_pokemon}</p>
                            <button className="btn btn-sm btn-danger" onClick={() => {deletePokemon(val.nickname)}}>Remove</button>
                        </div>
                    </div>
                )
            })}
        </div>
    )
}
